import React from 'react';


const ProfileStatusWithHooks = (props) => {
	return (
		<>
			{!this.state.editMode ?
				<div><span onDoubleClick={this.activateEditMode}>{props.profileStatus}</span></div>
				:
				<div><input autoFocus={true} onBlur={this.deactivateEditMode} onChange={(e) => {
					this.statusOnChangeHandle(e)
				}} value={''}/></div>
			}
			{this.state.isEmpty ? <button onClick={this.activateEditMode}>Поставить Статус</button> : ''}
		</>
	);
}

export default ProfileStatusWithHooks;
