import React from "react";
import s from "./FormsControls.module.css";

const FormControl = ({input, meta, element, ...props}) => {
	const hasError = meta.touched && meta.error;

	let newFormElement = React.createElement(element, {...input, ...props}, '');

	return (
		<div className={s.formControl + ' ' + (hasError ? s.error : '')}>
			{newFormElement}
			<br/>
			{ hasError && <span className={s.errorMsg}> {meta.error} </span> }
		</div>
	);
};

export const Textarea = (props) => {
	return <FormControl {...props} element={'textarea'} />;
};
export const Input = (props) => {
	return <FormControl {...props} element={'input'} />;
};
