import React from 'react';
import s from './ProfileInfo.module.css';

const ProfileInfo = (props) => {
	console.log(props);
	return (
		<div>
			<div className={s.bg}>
				<img src={props.photos.large} alt=""/>
			</div>
			<div className={s.descriptionBlock}>
				{props.aboutMe}
			</div>
			<ul>
				{
					props.contacts.map(c =>
						<li>
							${c}
						</li>
					)
				}
			</ul>
		</div>
	);
};

export default ProfileInfo;
