	let addPost = () => {
		alert('samuraijs.com');
	};

	return (
		<div className={s.myPostsBlock}>
			My posts
			<div className={s.addPost}>
				<textarea></textarea><br/>
				<button onClick={ addPost }>Add post</button>
				<button>Remove</button>
			</div>
			<div className={s.posts}>
				{postsElements}
			</div>
		</div>
	);
};

export default MyPosts;
