import React from 'react';


class ProfileStatus extends React.Component {
	state = {
		editMode: false
	};

	componentDidMount() {
		console.log(this.props);
	};

	render() {
		return (
			<>
				{!this.state.editMode ?
					<div><span>{this.props.status}</span></div>
					:
					<div><input value={this.props.status}/></div>
				}
			</>
		);
	}
}



export default ProfileStatus;
