import React from 'react';
import ProfileStatus from "./ProfileStatus";
import {connect} from "react-redux";
import {setUserProfileStatus} from "../../../../redux/profileReducer";


class ProfileStatusContainer extends React.Component {
	updateUserProfileStatus = () => {
		this.setUserProfileStatus(this.state.profileStatus);
	};

	render() {
		return <ProfileStatus {...this.props} />;
	}
}

const mapStateToProps = (state) => {
	return {
		profileStatus: state.profilePage.profileStatus
	}
};
const mapDispatchToProps = {
	setUserProfileStatus
};

export default connect(mapStateToProps, mapDispatchToProps)(ProfileStatus);
