import React from 'react';
import s from './MyPosts.module.css';
import Post from "./Post/Post";
import { Field, reduxForm } from 'redux-form';

class MyPosts extends React.Component {

	constructor(props) {
		super(props);

		this.postsElements =
			this.state.posts.map( p => <Post name={p.name} status={p.status} message={p.message} key={p.id} likesCount={p.likesCount}/>);

		this.state = {
			posts: this.props.posts
		};
	}

	componentDidMount() {
		console.log('MOUNTED');
	}

	componentDidUpdate(prevProps, prevState, snapshot) {
		console.log(this.postsElements);
		if (prevProps.posts.length !== this.props.posts.length) {
			this.setState({
				posts: this.props.posts
			});
		}
	}

	onSubmit = (formData) => {
		this.props.addPost(formData.newPostText);
		formData.newPostText = '';
	};

	render() {
		return (
			<div className={s.myPostsBlock}>
				My posts
				<div className={s.addPost}>
					<AddPostReduxForm onSubmit={this.onSubmit}/>
				</div>
				<div className={s.posts}>
					{this.postsElements}
				</div>
			</div>
		);
	}
}

const AddPostForm = (props) => {
	return <form onSubmit={props.handleSubmit}>
		<Field name={'newPostText'} component={'textarea'}/><br/>
		<button type={'submit'}>Add post</button>
	</form>;
};

const AddPostReduxForm = reduxForm({
	form: 'ProfileAddPost'
})(AddPostForm);

export default MyPosts;
