import React from "react";
import {connect} from "react-redux"
import Users from "./Users";
import Preloader from "../common/Preloader/Preloader";
import {
	follow,
	setCurrentPage,
	setTotalUsersCount,
	setUsers,
	showMore, turnOffPreloader, turnOnPreloader,
	unfollow
} from "../../redux/usersReducer";
import {usersAPI} from "../../api/api";


class UsersContainer extends React.Component {
	followUser = (userId) => {
		usersAPI.followUser(userId)
			.then(response => {
				if (response.resultCode === 0) {
					this.props.follow(userId);
				}
			});
	};
	unFollowUser = (userId) => {
		usersAPI.unfollowUser(userId)
			.then(response => {
				if (response.resultCode === 0) {
					this.props.unfollow(userId);
				}
			});
	};
	showMore = () => {
		this.props.showMore();
	};
	setCurrentPage = (currentPage) => {
		if (currentPage !== this.props.currentPage) {
			this.props.setCurrentPage(currentPage);
			this.getUsers(currentPage);
		}
	};

	turnOnPreloader = () => {
		this.props.turnOnPreloader();
	};
	turnOffPreloader = () => {
		this.props.turnOffPreloader();
	};

	getUsers(currentPage) {
		this.turnOnPreloader();
		usersAPI.getUsers(currentPage, this.props.pageSize)
			.then(response => {
				this.props.setUsers(response.items);
				this.props.setTotalUsersCount(response.totalCount);
			})
			.then(() => {
				this.turnOffPreloader();
			});
	};

	componentDidMount() {
		if (this.props.users.length === 0) {
			this.getUsers(this.props.currentPage);
		}
	};


	render() {
		return <>
			{this.props.isFetching ? <Preloader/> : null}
			<Users
				totalUsersCount={this.props.totalUsersCount}
				pageSize={this.props.pageSize}
				currentPage={this.props.currentPage}
				users={this.props.users}
				isFetching={this.props.isFetching}
				showMore={this.showMore}
				unFollowUser={this.unFollowUser}
				followUser={this.followUser}
				setCurrentPage={this.setCurrentPage}
				toggleFollowingProgress={this.props.followingInProgress}
			/>
		</>
	}
}

const mapStateToProps = (state) => {
	return {
		users: state.usersPage.users,
		showUsers: state.usersPage.showUsers,
		pageSize: state.usersPage.pageSize,
		totalUsersCount: state.usersPage.totalUsersCount,
		currentPage: state.usersPage.currentPage,
		isFetching: state.usersPage.isFetching,
		toggleFollowingInProgress: state.usersPage.toggleFollowingInProgress
	}
};

const mapDispatchToProps = {
	follow, unfollow,
	showMore, setUsers,
	setTotalUsersCount, setCurrentPage,
	turnOnPreloader, turnOffPreloader,
	followingInProgress
};

export default connect(mapStateToProps, mapDispatchToProps)(UsersContainer);
