const SHOW_MORE = 'SHOW-MORE',
	SET_USERS = 'SET-USERS',
	FOLLOW = 'FOLLOW',
	UNFOLLOW = 'UNFOLLOW';

let initialState = {
	users: [
		{
			id: 1,
			img: 'https://cdn1.iconfinder.com/data/icons/IconsLandVistaPeopleIconsDemo/256/Person_Undefined_Female_Light.png',
			name: 'User 1',
			location: {
				country: 'Ukraine',
				city: 'Kyiv',
			},
			status: 'Hello world!',
			followStatus: 'false'
		},
		{
			id: 2,
			img: 'https://cdn1.iconfinder.com/data/icons/IconsLandVistaPeopleIconsDemo/256/Client_Female_Light.png',
			name: 'User 2',
			location: {
				country: 'Ukraine',
				city: 'Kyiv',
			},
			status: 'Hello world!',
			followStatus: 'true'
		},
		{
			id: 3,
			img: 'http://icons.veryicon.com/png/System/Must%20Have/User.png',
			name: 'User 3',
			location: {
				country: 'Ukraine',
				city: 'Kyiv',
			},
			status: 'Hello world!',
			followStatus: 'false'
		},
		{
			id: 4,
			img: 'http://icons.iconarchive.com/icons/gianni-polito/colobrush/256/system-user-man-icon.png',
			name: 'User 4',
			location: {
				country: 'Ukraine',
				city: 'Kyiv',
			},
			status: 'Hello world!',
			followStatus: 'true'
		},
		{
			id: 5,
			img: 'https://upload.wikimedia.org/wikipedia/commons/thumb/1/12/User_icon_2.svg/220px-User_icon_2.svg.png',
			name: 'User 5',
			location: {
				country: 'Ukraine',
				city: 'Chabani',
			},
			status: 'Hello!',
			followStatus: 'false'
		},
	],
	showUsers: 2
};

const usersReducer = (state = initialState, action) => {

	switch (action.type) {
		case FOLLOW:
			return {
				...state,
				users: state.users.map( u => {
					if (u.id === action.userId) {
						return {...u, followStatus: true};
					}
				}),
			};

		case UNFOLLOW:
			return {
				...state,
				users: state.users.map( u => {
					if (u.id === action.userId) {
						return {...u, followStatus: false};
					}
				}),
			};

		case SET_USERS:
			return {
				...state,
				users: [ ...state.users , ...action.users ]
			};

		case SHOW_MORE:
			return {
				...state,
				showUsers: state.showUsers + 2
			};
		default:
			return state;
	}
};

export default usersReducer;

export const followAC = (userId) => ({type: FOLLOW, userId: userId});
export const unfollowAC = (userId) => ({type: UNFOLLOW, userId: userId});
export const setUsersAC = (users) => ({type: SET_USERS, userId: users});
export const showMoreAC = () => ({type: SHOW_MORE});
