import React from 'react';


class ProfileStatus extends React.Component {
	state = {
		editMode: false
		// profileStatus: this.props.status
	};

	componentDidMount() {
		console.log(this.props);
	};

	toggleEditMode = () => {
		this.setState({
			editMode: !this.state.editMode
		});
	};



	render() {
		return (
			<>
				{!this.state.editMode ?
					<div><span onDoubleClick={ this.toggleEditMode }>{this.state.profileStatus}</span></div>
					:
					<div><input autoFocus={true} onBlur={ this.toggleEditMode } onChange={ () => {} } value={this.state.profileStatus}/></div>
				}
			</>
		);
	}
}

export default ProfileStatus;
