import React from 'react';
import s from './Users.module.css';
import * as axios from 'axios';
import userPhoto from '../../assets/images/nophoto.jpg';


class Users extends React.Component {
	constructor(props){
		super(props);

		this.totalCount = 0;
		this.pageSize = 5;
	}

	followUser = (userId) => {
		this.props.follow(userId);
	};
	unFollowUser = (userId) => {
		this.props.unFollow(userId);
	};
	showMore = () => {
		this.props.showMore();
	};
	createPagination = () => {

	};

	componentDidMount() {
		if (this.props.users.length === 0) {
			axios.get(`https://social-network.samuraijs.com/api/1.0/users`).then(response => {
				this.props.setUsers(response.data.items);
				this.totalCount = response.data.totalCount;
			});
		}
	}

	getUsers = () => {

	};


	render() {
		let usersList = this.props.users.slice(0, this.props.showUsers).map( u =>
			<div className={s.user_block} key={u.id}>
				<div className={s.pic_block}>
					<div className={s.pic}>
						<img src={u.photos.small !== null ? u.photos.small : userPhoto} alt=""/>
					</div>
					{ u.followed
						? <button className={`${s.follow_btn} ${s.unfollow}`} onClick={ () => {this.unFollowUser(u.id) } }>Unfollow</button>
						: <button className={`${s.follow_btn} ${s.follow}`} onClick={ () => {this.followUser(u.id) } }>Follow</button>
					}
				</div>
				<div className={s.info}>
					<span className={s.name}>{u.name}</span>
					<span className={s.status}>{u.status}</span>
				</div>
			</div>
		);

		let pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize);

		let pages = [];

		for (let i = 1;i !== pagesCount; i++) {
			pages.push(i);
		}

		return (
			<div>
				<ul className={s.pagination}>
					{pages.map(p => {
						return <li className={p === this.props.currentPage ? s.selected_page : ''}>{p}</li>
					})}
				</ul>
				<div className={s.users}>
					{usersList}
					<button className={s.show_more} onClick={this.showMore}>Show more</button>
				</div>
			</div>
		);
	}
}

export default Users;
