import React from 'react';
import s from './../Dialogs.module.css';

const Message = (props) => {
	let msg = React.createRef();

	let sendMsg = () => {
		console.log(msg.current.value);
	};

	return (
		<div className={s.message}>
			{props.message}
		</div>
	);
};

export default Message;
