import React from 'react';
import s from './Dialogs.module.css';
import DialogItem from "./Dialog/DialogItem";
import Message from "./Message/Message";
import { Field, reduxForm } from 'redux-form';

const Dialogs = (props) => {
	let state = props.dialogsPage;

	let dialogElements = state.dialogs.map(d => <DialogItem id={d.id} key={d.id} name={d.name}/>);

	let messageElements = state.messages.map(m => <Message message={m.message}  key={m.id}/>);

	let msg = React.createRef();

	let onSendMsg = () => {
		props.sendMsg();
	};

	let onSubmit = (formData) => {
		console.log(formData);
	}

	return (
		<div className={s.dialogs}>
			<div className={s.dialogsItems}>
				{dialogElements}
			</div>
			<div>
				<div className={s.messages}>
					{messageElements}
				</div>
				<AddMessageFormRedux onSubmit={onSubmit} state={state} />
			</div>
		</div>
	)
};

const AddMessageForm = (props) => {
	console.log(props);

	return <form onSubmit={props.handleSubmit}>
		<Field component={'textarea'} name={'message'} value={props.newMessageBody}/>
		<button type={'submit'}>Send</button>
	</form>
};

const AddMessageFormRedux = reduxForm({
	form: 'dialogAddMessageForm'
})(AddMessageForm);

export default Dialogs;
