import React from 'react';
import s from "./Users.module.css";
import userPhoto from '../../assets/images/nophoto.jpg';
import {NavLink} from "react-router-dom";
import Paginator from "./Pagination";

let Users = React.memo((props) => {

	let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);

	let pages = [];

	for (let i = 1; i <= pagesCount; i++) {
		pages.push(i);
	}

	return (
		<div>
			<Paginator currentPage={props.currentPage} totalUsersCount={props.totalUsersCount} setCurrentPage={props.setCurrentPage} pageSize={props.pageSize}/>
			<div className={s.users}>
				{
					props.users.slice(0, props.showUsers).map(u =>
						<div className={s.user_block} key={u.id}>
							<div className={s.pic_block}>
								<div className={s.pic}>
									<NavLink to={`/profile/${u.id}`}>
										<img src={u.photos.small !== null ? u.photos.small : userPhoto} alt=""/>
									</NavLink>
								</div>
								{u.followed
									?
									<button disabled={props.followingInProgress.some(id => id === u.id)}
											className={`${s.follow_btn} ${s.unfollow}`}
											onClick={() => {
												props.toggleFollow(false, u.id);
											}}>Unfollow</button>
									:
									<button disabled={props.followingInProgress.some(id => id === u.id)}
											className={`${s.follow_btn} ${s.follow}`}
											onClick={() => {
												props.toggleFollow(true, u.id);
											}}>Follow</button>
								}
							</div>
							<div className={s.info}>
								<span className={s.name}>{u.name}</span>
								<span className={s.status}>{u.status}</span>
							</div>
						</div>
					)}
			</div>
		</div>
	);
});

export default Users;
