import React from 'react';
import s from './MyPosts.module.css';
import Post from "./Post/Post";
import { Field, reduxForm } from 'redux-form';

class MyPosts extends React.Component {
	constructor(props) {
		super(props);
		this.postsElements =
			this.props.posts.map( p => <Post name={p.name} status={p.status} message={p.message} key={p.id} likesCount={p.likesCount}/>);
	}

	onAddPost = (formData) => {
		console.log(formData);
		this.props.addPost(formData.postText);
	};

	render() {
		return (
			<div className={s.myPostsBlock}>
				My posts
				<div className={s.addPost}>
					<AddPostReduxForm onSubmit={this.onAddPost}/>
				</div>
				<div className={s.posts}>
					{this.postsElements}
				</div>
			</div>
		);
	}
}

const AddPostForm = (props) => {
	return <form onSubmit={props.handleSubmit}>
		<Field name={'postText'} component={'textarea'}/><br/>
		<button>Add post</button>
	</form>;
};

const AddPostReduxForm = reduxForm({
	form: 'ProfileAddPost'
})(AddPostForm);

export default MyPosts;
