import React from 'react';


class ProfileStatus extends React.Component {
	state = {
		editMode: false
	};

	componentDidMount() {

	};

	toggleEditMode = () => {
		this.setState({
			editMode: !this.state.editMode
		});
	};



	render() {
		return (
			<>
				{!this.state.editMode ?
					<div><span onDoubleClick={ this.toggleEditMode }>{this.props.profileStatus}</span></div>
					:
					<div><input autoFocus={true} onBlur={ this.toggleEditMode } onChange={ () => {} } value={this.props.profileStatus}/></div>
				}
				{!this.props.profileStatus ? <button onClick={ this.toggleEditMode }>Поставить Статус</button> : ''}
			</>
		);
	}
}

export default ProfileStatus;
