import React from 'react';

import './App.css';
import Header from "./components/Header/Header";
import Navbar from "./components/Navbar/Navbar";
import Profile from "./components/Profile/Profile";
import Dialogs from "./components/Dialogs/Dialogs";
import News from "./components/News/News";
import {BrowserRouter, Route} from "react-router-dom"
import {updateNewPostText} from "./redux/state";

const App = (props) => {
	let componentsDialogs = () => <Dialogs
			state={props.store.getState.dialogsPage}/>,

		componentsProfile = () => <Profile
			state={props.store.getState.profilePage}
			addPost={props.store.addPost}
			updateNewPostText={props.store.updateNewPostText}/>;

	return (
		<div className='app-wrapper'>

				<Header/>

				<Navbar state={props.state.sidebar}/>

				<div className='app-wrapper-content'>
					{/*<Route path={`/dialogs`} component={Dialogs}/>*/}
					{/*<Route path={`/profile`} component={Profile}/>*/}

					<Route path={`/news`} component={News}/>


					<Route path={`/dialogs`} render={componentsDialogs}/>
					<Route path={`/profile`} render={componentsProfile}/>
				</div>
			</div>
	);
};

export default App;
